// FeedbackForm.vue
<template>
  <div>
    <div class="rating-box">
      <h1>Rate Our Service?</h1>
      <p>Give us a quick rating so we know if you like it.</p>
      <div class="stars">
        <i v-for="(star, index) in 5" :key="index" class="bx bxs-star"
           :class="{'active': index < rating}"
           @click="setRating(index + 1)"></i>
        <div v-for="(star, index) in 5" :key="index" class="star-number">{{ index + 1 }}</div>
      </div>
      <button @click="submitFeedback">Submit</button>
    </div>
  </div>
</template>

<script>
import axios from 'axios';

export default {
  data() {
    return {
      rating: 0
    }
  },
  methods: {
  setRating(rating) {
    this.rating = rating;
  },
  async submitFeedback() {
    try {
      const response = await axios.post('http://localhost:8080/feedback/', {
        rating: this.rating
      });
      console.log('Response:', response);
      alert('Feedback submitted successfully');
    } catch (error) {
      console.error('Error submitting feedback:', error.response ? error.response.data : error.message);
      alert('Failed to submit feedback');
    }
  }
}

}
</script>

<style scoped>
.stars {
  display: flex;
  align-items: center;
  gap: 10px;
}
.bx {
  color: grey;
  font-size: 40px;
  cursor: pointer;
  transition: color .2s ease;
}
.bx.active {
  color: yellow;
}
.star-number {
  color: grey;
  font-size: 20px;
  text-align: center;
  margin-top: 5px;
}
</style>
